var link=document.querySelector(".search-caption"),popup=document.querySelector(".widget"),arrival=popup.querySelector("[name=data-arrival]"),form=popup.querySelector("form"),validation=popup.querySelectorAll(".validation");link.addEventListener("click",function(a){a.preventDefault();popup.classList.toggle("modal-show");arrival.focus()});window.addEventListener("keydown",function(a){27===a.keyCode&&(a.preventDefault(),popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error")))});
form.addEventListener("submit",function(a){for(var b=0;b<validation.length;b++)validation[b].value||(a.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))});